import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

function Calendar() {
  const [events, setEvents] = useState([
    {
      title: "The title",
      start: "2024-08-18T08:00:00",
      end: "2024-08-19T09:00:00",
    },
  ]);

  const [modalOpen, setModalOpen] = useState(false);
  const [newEvent, setNewEvent] = useState({
    title: "",
    start: "",
    end: "",
  });

  useEffect(() => {
    // Fetch global events (e.g., public holidays)
    fetch("62777a1b-0e6b-4801-af1b-9cdc0f638e3c") // Replace with actual API endpoint
      .then((response) => response.json())
      .then((data) => {
        const globalEvents = data.holidays.map((holiday) => ({
          title: holiday.name,
          start: holiday.date,
        }));
        setEvents((prevEvents) => [...prevEvents, ...globalEvents]);
      })
      .catch((error) => console.error("Error fetching global events:", error));
  }, []);

  const handleDateClick = (arg) => {
    setNewEvent({ ...newEvent, start: arg.dateStr, end: arg.dateStr });
    setModalOpen(true);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEvent({ ...newEvent, [name]: value });
  };

  const handleAddEvent = () => {
    setEvents([...events, newEvent]);
    setModalOpen(false);
  };

  return (
    <div>
      <button onClick={() => setModalOpen(true)}>Add Event</button>

      {modalOpen && (
        <div>
          <div>
            <h2>Add Event</h2>
            <input
              type="text"
              name="title"
              placeholder="Event Title"
              value={newEvent.title}
              onChange={handleInputChange}
            />
            <input
              type="datetime-local"
              name="start"
              placeholder="Start Date"
              value={newEvent.start}
              onChange={handleInputChange}
            />
            <input
              type="datetime-local"
              name="end"
              placeholder="End Date"
              value={newEvent.end}
              onChange={handleInputChange}
            />
            <button onClick={handleAddEvent}>Add Event</button>
          </div>
        </div>
      )}

      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView={"dayGridMonth"}
        headerToolbar={{
          start: "today prev,next",
          center: "title",
          end: "dayGridMonth,timeGridWeek,timeGridDay",
        }}
        height={"90vh"}
        events={events}
        dateClick={handleDateClick}
      />
    </div>
  );
}

export default Calendar;
