import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

function Calendar() {
  const [events, setEvents] = useState([]);
  const [userEvents, setUserEvents] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [newEvent, setNewEvent] = useState({
    title: "",
    start: "",
    end: "",
  });

  useEffect(() => {
    // Fetch global events
    fetch(
      "https://calendarific.com/api/v2/holidays?&api_key=VSGAKMQUk4PbTNboNbnUjzfrHtAesMKl&country=IN&year=2024"
    )
      .then((response) => response.json())
      .then((data) => {
        if (data.response && data.response.holidays) {
          const globalEvents = data.response.holidays.map((holiday) => ({
            title: holiday.name,
            start: holiday.date.iso,
          }));
          setEvents(globalEvents);
        }
      })
      .catch((error) => console.error("Error fetching global events:", error));

    // Load user-added events from localStorage
    const savedUserEvents = JSON.parse(localStorage.getItem('userEvents')) || [];
    setUserEvents(savedUserEvents);
  }, []);

  useEffect(() => {
    // Update localStorage whenever userEvents changes
    localStorage.setItem('userEvents', JSON.stringify(userEvents));
  }, [userEvents]);

  const handleAddEventClick = () => {
    setModalOpen(true);
  };

  const handleDateClick = (arg) => {
    // Optional: You can set the start and end date for the new event here if needed
    setNewEvent({ ...newEvent, start: arg.dateStr, end: arg.dateStr });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEvent({ ...newEvent, [name]: value });
  };

  const handleAddEvent = () => {
    const eventToAdd = { ...newEvent, id: new Date().toISOString() }; // Add a unique ID to the event
    setUserEvents([...userEvents, eventToAdd]);
    setModalOpen(false);
  };

  const handleRemoveEvent = (eventToRemove) => {
    const updatedEvents = userEvents.filter(
      (event) => event.id !== eventToRemove.id
    );
    setUserEvents(updatedEvents);
  };

  const renderEventContent = (eventInfo) => {
    const isUserEvent = userEvents.some(
      (event) =>
        event.start === eventInfo.event.startStr && event.title === eventInfo.event.title
    );

    return (
      <div>
        <span>{eventInfo.event.title}</span>
        {isUserEvent && (
          <button onClick={() => handleRemoveEvent(eventInfo.event)}>Remove</button>
        )}
      </div>
    );
  };

  return (
    <div>
      <button onClick={handleAddEventClick}>Add Event</button>

      {modalOpen && (
        <div>
          <div>
            <h2>Add Event</h2>
            <input
              type="text"
              name="title"
              placeholder="Event Title"
              value={newEvent.title}
              onChange={handleInputChange}
            />
            <input
              type="datetime-local"
              name="start"
              placeholder="Start Date"
              value={newEvent.start}
              onChange={handleInputChange}
            />
            <input
              type="datetime-local"
              name="end"
              placeholder="End Date"
              value={newEvent.end}
              onChange={handleInputChange}
            />
            <button onClick={handleAddEvent}>Add Event</button>
          </div>
        </div>
      )}

      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView={"dayGridMonth"}
        headerToolbar={{
          start: "today prev,next",
          center: "title",
          end: "dayGridMonth,timeGridWeek,timeGridDay",
        }}
        height={"90vh"}
        events={[...events, ...userEvents]}
        eventContent={renderEventContent}
      />
    </div>
  );
}

export default Calendar;
