import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

function Calendar() {
  const [events, setEvents] = useState([]); // All events including global and user-added
  const [userEvents, setUserEvents] = useState([]); // Only user-added events
  const [modalOpen, setModalOpen] = useState(false);
  const [newEvent, setNewEvent] = useState({
    title: "",
    start: "",
    end: "",
  });

  useEffect(() => {
    fetch(
      "https://calendarific.com/api/v2/holidays?&api_key=VSGAKMQUk4PbTNboNbnUjzfrHtAesMKl&country=IN&year=2024"
    )
      .then((response) => response.json())
      .then((data) => {
        if (data.response && data.response.holidays) {
          const globalEvents = data.response.holidays.map((holiday) => ({
            title: holiday.name,
            start: holiday.date.iso,
          }));
          setEvents(globalEvents);
        }
      })
      .catch((error) => console.error("Error fetching global events:", error));
  }, []);

  const handleDateClick = (arg) => {
    setNewEvent({ ...newEvent, start: arg.dateStr, end: arg.dateStr });
    setModalOpen(true);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEvent({ ...newEvent, [name]: value });
  };

  const handleAddEvent = () => {
    setUserEvents([...userEvents, newEvent]);
    setEvents((prevEvents) => [...prevEvents, newEvent]);
    setModalOpen(false);
  };

  const handleRemoveEvent = (eventToRemove) => {
    setUserEvents((prevEvents) =>
      prevEvents.filter(
        (event) => event.start !== eventToRemove.start || event.title !== eventToRemove.title
      )
    );
    setEvents((prevEvents) =>
      prevEvents.filter(
        (event) => event.start !== eventToRemove.start || event.title !== eventToRemove.title
      )
    );
  };

  const renderEventContent = (eventInfo) => {
    // Show remove button only for user-added events
    if (userEvents.some(event => event.start === eventInfo.event.startStr && event.title === eventInfo.event.title)) {
      return (
        <div>
          <span>{eventInfo.event.title}</span>
          <button onClick={() => handleRemoveEvent(eventInfo.event)}>Remove</button>
        </div>
      );
    }
    return <div>{eventInfo.event.title}</div>;
  };

  return (
    <div>
      <button onClick={() => setModalOpen(true)}>Add Event</button>

      {modalOpen && (
        <div>
          <div>
            <h2>Add Event</h2>
            <input
              type="text"
              name="title"
              placeholder="Event Title"
              value={newEvent.title}
              onChange={handleInputChange}
            />
            <input
              type="datetime-local"
              name="start"
              placeholder="Start Date"
              value={newEvent.start}
              onChange={handleInputChange}
            />
            <input
              type="datetime-local"
              name="end"
              placeholder="End Date"
              value={newEvent.end}
              onChange={handleInputChange}
            />
            <button onClick={handleAddEvent}>Add Event</button>
          </div>
        </div>
      )}

      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView={"dayGridMonth"}
        headerToolbar={{
          start: "today prev,next",
          center: "title",
          end: "dayGridMonth,timeGridWeek,timeGridDay",
        }}
        height={"90vh"}
        events={events}
        dateClick={handleDateClick}
        eventContent={renderEventContent}
      />
    </div>
  );
}

export default Calendar;
