import React, { useState, useEffect, useCallback, useRef } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import debounce from 'lodash.debounce';

// Fetch global events from the API
const fetchGlobalEvents = async () => {
  try {
    const response = await fetch(
      "https://calendarific.com/api/v2/holidays?&api_key=VSGAKMQUk4PbTNboNbnUjzfrHtAesMKl&country=IN&year=2024"
    );
    const data = await response.json();
    if (data.response && data.response.holidays) {
      return data.response.holidays.map(holiday => ({
        title: holiday.name,
        start: holiday.date.iso,
        isGlobal: true
      }));
    }
    return [];
  } catch (error) {
    console.error("Error fetching global events:", error);
    return [];
  }
};

const Calendar = () => {
  const [events, setEvents] = useState([]);
  const [userEvents, setUserEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [newEvent, setNewEvent] = useState({ title: '', start: '', end: '' });
  const [filter, setFilter] = useState('');
  const [currentDate, setCurrentDate] = useState(new Date()); // State for the current date
  const calendarRef = useRef(null);

  // Load events on component mount
  useEffect(() => {
    const loadEvents = async () => {
      const globalEvents = await fetchGlobalEvents();
      setEvents(globalEvents);

      const savedUserEvents = JSON.parse(localStorage.getItem('userEvents')) || [];
      setUserEvents(savedUserEvents);
      setFilteredEvents([...globalEvents, ...savedUserEvents]);
    };

    loadEvents();
  }, []);

  // Update filtered events and localStorage when events or filter change
  useEffect(() => {
    localStorage.setItem('userEvents', JSON.stringify(userEvents));
    const allEvents = [...events, ...userEvents];
    const filtered = allEvents.filter(event =>
      event.title.toLowerCase().includes(filter.toLowerCase())
    );
    setFilteredEvents(filtered);
  }, [userEvents, events, filter]);

  // Update calendar view to current date
  useEffect(() => {
    if (calendarRef.current) {
      const calendarApi = calendarRef.current.getApi();
      calendarApi.gotoDate(currentDate); // Navigate to the current date
    }
  }, [currentDate]);

  const handleAddEventClick = useCallback(() => {
    setModalOpen(true);
  }, []);

  const handleDateClick = useCallback((arg) => {
    if (!modalOpen) {
      setNewEvent({ ...newEvent, start: arg.dateStr, end: arg.dateStr });
      setModalOpen(true);
    }
  }, [modalOpen, newEvent]);

  const handleInputChange = useCallback((e) => {
    const { name, value } = e.target;
    setNewEvent(prev => ({ ...prev, [name]: value }));
  }, []);

  const handleAddEvent = useCallback(() => {
    if (newEvent.title && newEvent.start && newEvent.end) {
      const eventToAdd = { ...newEvent, id: new Date().toISOString(), isGlobal: false };
      setUserEvents(prev => [...prev, eventToAdd]);
      setModalOpen(false);
      setNewEvent({ title: '', start: '', end: '' });
    } else {
      alert('Please fill in all fields');
    }
  }, [newEvent]);

  const handleFilterChange = useCallback(debounce((e) => {
    setFilter(e.target.value);
    // Update current date when filter is applied
    setCurrentDate(new Date()); 
  }, 300), []);

  return (
    <div>
      <input
        type="text"
        placeholder="Filter events"
        value={filter}
        onChange={handleFilterChange} // Debounced function
      />
      <button onClick={handleAddEventClick}>Add Event</button>

      {modalOpen && (
        <div>
          <h2>Add Event</h2>
          <input
            type="text"
            name="title"
            placeholder="Event Title"
            value={newEvent.title} // Ensure value is controlled
            onChange={handleInputChange}
          />
          <input
            type="datetime-local"
            name="start"
            placeholder="Start Date"
            value={newEvent.start} // Ensure value is controlled
            onChange={handleInputChange}
          />
          <input
            type="datetime-local"
            name="end"
            placeholder="End Date"
            value={newEvent.end} // Ensure value is controlled
            onChange={handleInputChange}
          />
          <button onClick={handleAddEvent}>Add Event</button>
        </div>
      )}

      <FullCalendar
        ref={calendarRef}
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        headerToolbar={{
          start: "today prev,next",
          center: "title",
          end: "dayGridMonth,timeGridWeek,timeGridDay",
        }}
        height="90vh"
        events={filteredEvents}
        dateClick={handleDateClick}
      />
    </div>
  );
};

export default Calendar;
